import React from 'react';
import Container from 'react-bootstrap/Container';
import Image from 'react-bootstrap/Image'

class BeastImage extends React.Component {
  constructor(props){
    super(props);
    this.state ={
      status: "Your Vote!"
    }
  }


  render() {
    return (
      <>
      <Container>
      <Image src={this.props.image} alt="The beaties and the horned beast" onClick={this.handleClick} />
      {/* onClick is the event listener and handleClick is the event handler, the event listener si the thing the attribute name  */}
      </Container>
      {/*  */}
      </>
    )

  }
}

export default BeastImage;
